const crypto = require('crypto');

function unauthorized(message, code) {
  return [{ json: { statusCode: 401, message, error: code } }];
}
function forbidden(message) {
  return [{ json: { statusCode: 403, message, error: 'FORBIDDEN' } }];
}

const header = this.getHeader('authorization');
if (!header || !header.startsWith('Bearer ')) return unauthorized('Token ausente.', 'TOKEN_MISSING');

const token = header.replace(/^Bearer\s+/i, '').trim();
const [h, p, s] = token.split('.');
if (!h || !p || !s) return unauthorized('Token malformado.', 'TOKEN_MALFORMED');

const recalculated = crypto.createHmac('sha256', process.env.JWT_SECRET).update(`${h}.${p}`).digest('base64url');
if (s !== recalculated) return unauthorized('Assinatura inválida.', 'TOKEN_INVALID_SIGNATURE');

const payload = JSON.parse(Buffer.from(p, 'base64url').toString('utf8'));
if (payload.exp && payload.exp < Math.floor(Date.now() / 1000)) return unauthorized('Token expirado.', 'TOKEN_EXPIRED');

if (payload.role !== 'ADMIN') return forbidden('Acesso negado. Somente administradores.');

return [{ json: { userId: payload.userId, role: payload.role, schoolId: payload.schoolId, statusCode: 200 } }];

const { ruleName, marksToAward } = $json;
const schoolId = $json.schoolId;

if (!ruleName || typeof marksToAward !== 'number') {
  return [{ json: { statusCode: 400, message: 'Campos obrigatórios: ruleName (string) e marksToAward (number).', error: 'VALIDATION_ERROR' } }];
}

const query = `
  INSERT INTO school_rules (id, school_id, rule_name, marks_to_award, created_at)
  VALUES (gen_random_uuid(), $1, $2, $3, NOW())
  RETURNING id, rule_name AS "ruleName", marks_to_award AS "marksToAward";
`;

return [{ json: { sql: query, values: [schoolId, ruleName, marksToAward] } }];
